import React, { useEffect, useState } from 'react';
import OwnProfile from './OwnProfile.js';
import OtherUserProfile from './OtherUserProfile.js';
import axios from 'axios';

function Profile() {
	const [isCurrentUser, setIsCurrentUser] = useState(false);
	const { username } = useParams(); // Get the username from the route parameters

	useEffect(() => {
		const token = localStorage.getItem('token');

		axios
			.get('http://localhost:3001/profile', {
				headers: {
					Authorization: `${token}`,
				},
			})
			.then((response) => {
				const authenticatedUsername = response.data.user.username;

				// Compare the usernames to determine if it's the current user's profile
				setIsCurrentUser(authenticatedUsername === username);
			})
			.catch((error) => {
				console.error('Error fetching user profile:', error);
			});
	}, [username]); // Run this effect whenever the username changes in the route parameters

	return <div>{isCurrentUser ? <OwnProfile /> : <OtherUserProfile />}</div>;
}

export default Profile;
