import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Navbar() {
	const isUserSignedIn = !!localStorage.getItem('token');
	const navigate = useNavigate();

	const handleProfileSearch = async (req, res) => {
		const token = localStorage.getItem('token');

		try {
			const result = await axios.get('http://localhost:3001/profile/:username', {
				headers: {
					Authorization: `${token}`,
				},
			});
		} catch (error) {}
	};

	const handleSignOut = () => {
		localStorage.removeItem('token');
		navigate('/login');
	};

	return (
		<nav className='bg-gradient-to-r from-f7f7f7 to-ffffff shadow-sm text-black px-6 py-4 flex items-center justify-between'>
			<Link to='/' className='font-bold text-xl tracking-wide'>
				Recolect
			</Link>
			<div className='ml-auto flex space-x-6'>
				{isUserSignedIn ? (
					<>
						<input
							type='text'
							placeholder='Search for a profile...'
							className='border border-gray-300 px-2 py-1 rounded-md focus:outline-none'
						/>
						<button
							className='hover:text-red-700 decoration-solid'
							onClick={handleSignOut}
						>
							Sign Out
						</button>
					</>
				) : (
					<>
						<Link
							to='/login'
							className='hover:text-blue-700 decoration-solid'
						>
							Log In
						</Link>
						<Link
							to='/signup'
							className='hover:text-blue-700 decoration-solid'
						>
							Sign Up
						</Link>
					</>
				)}
			</div>
		</nav>
	);
}

export default Navbar;
