import React, { useEffect, useState } from 'react';
import OwnProfile from './OwnProfile.js';
import OtherUserProfile from './OtherUserProfile.js';
import axios from 'axios';

function Profile() {
	const [isCurrentUser, setIsCurrentUser] = useState(false);

	useEffect(() => {
		const token = localStorage.getItem('token');

		// Make a request to the server to get the authenticated user's information
		axios
			.get('http://localhost:3001/profile', {
				headers: {
					Authorization: `${token}`,
				},
			})
			.then((res) => {
				const authenticatedUsername = res.data.user.username;

				// Assuming you have a way to get the username of the profile being viewed
				const requestedUsername =
					// Compare the usernames to determine if it's the current user's profile
					setIsCurrentUser(
						authenticatedUsername === requestedUsername
					);
			})
			.catch((error) => {
				console.error('Error fetching user profile:', error);
			});
	}, []); // Only run this effect once when the component mounts

	return <div>{isCurrentUser ? <OwnProfile /> : <OtherUserProfile />}</div>;
}

export default Profile;
